import React from 'react';

export default class MobileFooter extends React.Component {
  constructor(props) {
    super(props);

    this.clickHandler = this.clickHandler.bind(this);
    this.changeHandler = this.changeHandler.bind(this);

    this.state = {
      email: '',
    };
  }

  async changeHandler(e) {
    await this.setState({ email: e.target.value });
    if (this.state.email) {
      document.querySelector('.email_submit').classList.remove('hide');
    } else {
      document.querySelector('.email_submit').classList.add('hide');
    }
  }

  clickHandler(e) {
    e.preventDefault();
  }

  render() {
    return (
      <footer>
        <div className="subscribe">
          <div className="container">
            <p>
              Abonnieren Sie Updates über die Arbeit von Open Society auf der
              ganzen Welt
            </p>
            <form>
              <input
                placeholder="Deine E-Mail-Adresse"
                type="text"
                className="email_form"
                value={this.state.email}
                onChange={this.changeHandler}
              />
              <button onClick={this.clickHandler} className="email_submit hide">
                Submit
              </button>
            </form>
          </div>
        </div>
        <div className="footer_footer">
          <div className="container">
            <div className="split">
              <div className="privacy">
                <a href="#">Geschäftsbedingungen</a>
                <a href="#">Privatsphäre</a>
                <a href="#">Barrierefreiheit</a>
              </div>
            </div>
          </div>
        </div>
      </footer>
    );
  }
}
